mapper.select:
select * from mk_user
and user_name like #{userName}
and EXISTS(select 1 from mk_user_role where mk_user_role.user_id = mk_user.id and role_id= #{roleId})

select c.*  from mk_user a join mk_user_role b on a.id = b.user_id
    join mk_role c on c.id = b.role_id
    where a.id=#{userId}
